import React, {useState} from 'react';

const ENDPT = "";

export default function Blog() {

  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = () => {
    setTimeout(() => {
      setSubmitted(true);
    }, 100);
  };

  if (submitted) {
    return (
      <>
        <div>Thank You,</div>
        <div>We'll be in touch soon.</div>
      </>
    )
  }

  return (
    <div>
      <h1>Contact Me</h1>
      <form
        action={ENDPT}
        onSubmit={handleSubmit}
        method="POST"
        target="_blank"
      >
        <div>
          <input
            type="text"
            placeholder="Your Name"
            name="name"
            required
          />
        </div>
        <div>
          <textarea
          />
        </div>
      </form>
    </div>
  );
}
